import "../../styles/Global.css";
import { Routes, Route, useNavigate } from "react-router-dom";
import Home from "../../components/welcome/Welcome";
import { useEffect, useState } from "react";
import logo from "../../assets/logom.png"
import Axios from "axios";
import Welcome from "../../components/welcome/Welcome";
import HomeView from "../home/HomeView";

export default function WelcomeView() {
  const [user, setUser] = useState(null);
  let navigate = useNavigate();
  	
  const userString = "" + user;
  const firstFour = userString.substring(0, 4);
  const lastFour = userString.substring(userString.length - 4);
  const userPreview = firstFour + "..." + lastFour;
  
  useEffect(() => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/user",
    }).then((res) => {
      res.data.id ? setUser(res.data.id) : setUser(null);
      console.log(res);
    })
  })

  const navigateHome = () => {
    navigate("/");
  };

  const navigateLogin = () => {
    window.location.replace("http://localhost:3001/login")
  };

  const navigateLogout = () => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/logout",
    }).then((res) => {
      setUser(null);
      console.log(res);
    })
  }

  return (
    <div>
      <nav>
        <img src={logo} className="imglogo"/>
        {user == null ? (
        <button className="loginbutton" onClick={navigateLogin}>
          Login
        </button>
        ) : (
          // Is Logged
        <div>
          <button className="loginbutton" onClick={navigateLogout}>
            {userPreview}
          </button>
        </div>
        )}
      </nav>
      <Routes>
        <Route path="/" element={<Welcome />} />
        <Route path="/" element={<HomeView />} />
      </Routes>
    </div>
  );
}
