import React, { useState } from 'react';
import { ComponenteA } from'./ComponentA'
import './ComponentB'

export default function HomeView(){
  const [selecao, setSelecao] = useState('');

  const handleSelecaoChange = (event) => {
    setSelecao(event.target.value);
  };

  let componenteRenderizado;

  switch (selecao) {
    case 'A':
      componenteRenderizado = <ComponenteA />;
      break;
    case 'B':
      componenteRenderizado = <ComponenteB />;
      break;
    default:
      componenteRenderizado = null;
  }

  return (
    <div>
      <select value={selecao} onChange={handleSelecaoChange}>
        <option value="">Selecione um componente</option>
        <option value="A">Componente A</option>
        <option value="B">Componente B</option>
      </select>

      {componenteRenderizado}
    </div>
  );
};
