import "../../styles/Global.css";
import { Routes, Route, useNavigate } from "react-router-dom";
import Home from "../../components/home/Home";
import { useEffect, useState } from "react";
import logo from "../../assets/logom.png";
import Axios from "axios";
import Modal from './Modal';

export default function HomeView() {
  const [user, setUser] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  let navigate = useNavigate();

  useEffect(() => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/user",
    }).then((res) => {
      setUser(res.data); // Ajustado para definir o usuário corretamente
      console.log(res);
    });
  }, []); // Adicionado um array vazio para garantir que o useEffect seja executado apenas uma vez

  const userString = "" + user;
  const firstFour = userString.substring(0, 4);
  const lastFour = userString.substring(userString.length - 4);
  const userPreview = firstFour + "..." + lastFour;

  const navigateHome = () => {
    navigate("/");
  };

  const navigateLogin = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  const navigateLogout = () => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/logout",
    }).then((res) => {
      setUser(null);
      console.log(res);
    });
  };

  return (
    <div>
      <nav>
        <img src={logo} className="imglogo" />
        {user == null ? (
          <button className="loginbutton" onClick={navigateLogin}>
            Login
          </button>
        ) : (
          <div>
            <button className="loginbutton" onClick={navigateLogout}>
              {userPreview}
            </button>

            <p>A</p>
          </div>
        )}
      </nav>

      {modalVisible && (
        <div className="overlay">
          <Modal onClose={closeModal} />
        </div>
      )}

      <Routes>
        <Route path="/" element={<Home />} />
      </Routes>
    </div>
  );
}
