import React, { useEffect, useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Home from "../../components/home/Home";
import logo from "../../assets/logom.png";
import Axios from "axios";
import Modal from "./Modal"; // Certifique-se de ter o componente Modal implementado

export default function HomeView() {
  const [user, setUser] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  let navigate = useNavigate();

  const userString = "" + user;
  const firstFour = userString.substring(0, 4);
  const lastFour = userString.substring(userString.length - 4);
  const userPreview = firstFour + "..." + lastFour;

  useEffect(() => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/user",
    }).then((res) => {
      res.data.id ? setUser(res.data.id) : setUser(null);
      console.log(res);
    });
  }, []);

  const navigateHome = () => {
    navigate("/");
  };

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  const navigateLogin = () => {
    window.location.replace("http://localhost:3001/login");
  };

  const navigateLogout = () => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/logout",
    }).then((res) => {
      setUser(null);
      openModal(); // Abre o modal ao fazer logout
      console.log(res);
    });
  };

  return (
    <div>
      <nav>
        <img src={logo} className="imglogo" />
        {user == null ? (
          <button className="loginbutton" onClick={navigateLogin}>
            Login
          </button>
        ) : (
          // Is Logged
          <div>
            <button className="loginbutton" onClick={navigateLogout}>
              {userPreview}
            </button>
          </div>
        )}
      </nav>

      {modalVisible && (
        <Modal onClose={closeModal}>
          {/* Conte√∫do do seu modal aqui */}
          <p>Modal Content</p>
        </Modal>
      )}

      <Routes>
        <Route path="/" element={<Home />} />
      </Routes>
    </div>
  );
}
