import "./App.css";
import { Routes, Route, useNavigate } from "react-router-dom";
import { MdLogin, MdLogout } from "react-icons/md";
import Home from "./components/home/Home";
import { useEffect, useState } from "react";
import logo from "./assets/logom.png"
import Axios from "axios";

function App() {
  const [user, setUser] = useState(null);
  let navigate = useNavigate();

  useEffect(() => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/user",
    }).then((res) => {
      res.data.id ? setUser(res.data.id) : setUser(null);
      console.log(res);
    })
  })

  const navigateHome = () => {
    navigate("/");
  };

  const navigateLogin = () => {
    window.location.replace("http://localhost:3001/login")
  };

  const navigateLogout = () => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/logout",
    }).then((res) => {
      setUser(null);
      console.log(res);
    })
  }

  return (
    <div>
      <nav>
        <img src={logo} className="imglogo"/>
        {user == null ? (
        <button className="loginbutton" onClick={navigateLogin}>
          Login
        </button>
        ) : (
        <div className="cpLayout" onClick={navigateLogout}>
          <p className="user">Logged in as: {user}</p>
          <h4 className="navCP">Logout</h4>
        </div>
        )}
      </nav>
      <Routes>
        <Route path="/" element={<Home />} />
      </Routes>
    </div>
  );
}

export default App;
