import React, { useState } from 'react';
import ComponenteA from '../../components/home/select/ComponentA'
import ComponenteB from '../../components/home/ComponentB'
import './Home.css'

export default function HomeView() {
    const [selecao, setSelecao] = useState('A');

    const handleSelecao = (opcao) => {
        setSelecao(opcao);
    };

    let componenteRenderizado;

    switch (selecao) {
        case 'A':
            componenteRenderizado = <ComponenteA />;
            break;
        case 'B':
            componenteRenderizado = <ComponenteB />;
            break;
        case 'C':
            break;
        default:
            componenteRenderizado = null;
    }

    return (
        <div className='containerhome'>
            <div className='containerselect'>
                <button
                    onClick={() => handleSelecao('A')}
                    className={selecao === 'A' ? 'button-onselect' : 'button-noselect'}
                >
                    CHAT HELPER DEVELOPERS
                </button>
                <button
                    onClick={() => handleSelecao('B')}
                    className={selecao === 'B' ? 'button-onselect' : 'button-noselect'}
                >
                    DOCUMENTATION
                </button>
                <button
                    onClick={() => handleSelecao('C')}
                    className={selecao === 'C' ? 'button-onselect' : 'button-noselect'}
                >
                    EXTENSION FOR IDE HELPER DEVELOPERS
                </button>
            </div>

            {selecao === 'A' && <ComponenteA />}
            {selecao === 'B' && <ComponenteB />}
        </div>
    );
};
