import "../../styles/Global.css";
import { Routes, Route, useNavigate } from "react-router-dom";
import Home from "../../components/home/Home";
import { useEffect, useState } from "react";
import logo from "../../assets/logom.png"
import Axios from "axios";

export default function HomeView() {
  const [user, setUser] = useState(null);
  let navigate = useNavigate();

  const userString = "021a888c03c689b47aee7dfab544951c72b3225ff79aea793c375b8c585c362369";
  const firstFour = hexString.substring(0, 4);
  const ultimos4 = hexString.substring(hexString.length - 4);
  const resultado = primeiros4 + "..." + ultimos4;
  console.log(resultado);
  

  useEffect(() => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/user",
    }).then((res) => {
      res.data.id ? setUser(res.data.id) : setUser(null);
      console.log(res);
    })
  })

  const navigateHome = () => {
    navigate("/");
  };

  const navigateLogin = () => {
    window.location.replace("http://localhost:3001/login")
  };

  const navigateLogout = () => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3001/logout",
    }).then((res) => {
      setUser(null);
      console.log(res);
    })
  }

  return (
    <div>
      <nav>
        <img src={logo} className="imglogo"/>
        {user == null ? (
        <button className="loginbutton" onClick={navigateLogin}>
          Login
        </button>
        ) : (
          // Is Logged
        <div>
          <p>{user}</p>
          <button className="loginbutton" onClick={navigateLogout}>
            Logout
          </button>
        </div>
        )}
      </nav>
      <Routes>
        <Route path="/" element={<Home />} />
      </Routes>
    </div>
  );
}
