import React, { useState } from 'react';
import ComponenteA from './ComponentA'
import ComponenteB from './ComponentB'
import './Home.css'

export default function HomeView(){
    const [selecao, setSelecao] = useState('');

    const handleSelecao = (opcao) => {
      setSelecao(opcao);
    };
  
    let componenteRenderizado;
  
    switch (selecao) {
      case 'A':
        componenteRenderizado = <ComponenteA />;
        break;
      case 'B':
        componenteRenderizado = <ComponenteB />;
        break;
      case 'C':
        break;
      default:
        componenteRenderizado = null;
    }

  return (
    <div>
      <div className='containerselect'>
        <button onClick={() => handleSelecao('A')}>Componente A</button>
        <button onClick={() => handleSelecao('B')}>Componente B</button>
        <button onClick={() => handleSelecao('C')}>Componente C</button>
      </div>

      {componenteRenderizado}
    </div>
  );
};
